$("#friends-list a").click((function(e){e.preventDefault();let s=$(this).text();$.ajax({type:"get",url:$(this).attr("href"),success:function(e){let a=$("<div></div>").appendTo($("#chatbox-flex"));if(a.append(` <h4> ${s} </h4>`),$(`<div id="chatbox-${e.data.chatbox._id}" class="chat-box">\n         \n          <div class="messages-container">\n          <ul class="messages">    \n          </ul>\n          </div>\n          \n          <div class="input-message-container">\n          <form class="input-message">\n              <input class="input-message-field" name="message" placeholder="Type Here" type="text">\n              <button type="submit">Send</button>\n          </form>\n          </div>\n          </div>`).appendTo(a),e.data.chat_messages)for(chat_message of e.data.chat_messages){let s;s=user_email!=chat_message.sender.email?$(`<li class="other-message">\n                    <p>\n                        ${chat_message.message}\n                    </p>\n                </li>`):$(`<li class="self-message">\n                    <p>\n                        ${chat_message.message}\n                    </p>\n                </li>`),s.appendTo(`#chatbox-${e.data.chatbox._id} .messages`)}new chatBox(e.data.chatbox._id,user_email,env_name)},error:function(e){console.log("Error",e)}})}));